const supabaseData = [];
// Using the user_id from the user's example as fallback. 
// WARNING: This ID must exist in auth.users for the foreign key constraint to pass.
const FALLBACK_USER_ID = '512c51e1-f250-46f0-826f-824ffe112c6a';

function mapStatus(status, connectionStatus) {
    let s = (connectionStatus || status || '').toLowerCase();
    if (s === 'open' || s === 'connected') return 'connected';
    if (s === 'close' || s === 'disconnected') return 'disconnected';
    if (s === 'connecting') return 'connecting';
    if (s === 'qr' || s === 'qrcode') return 'qr_code';
    return 'disconnected';
}

function processInstance(inst) {
    const name = inst.instanceName || inst.name;

    if (name) {
        const now = new Date().toISOString();
        supabaseData.push({
            name: name,
            evolution_instance_id: inst.instanceId || inst.id || inst.name,
            phone: inst.phoneNumber || (inst.ownerJid && inst.ownerJid.includes('@') ? inst.ownerJid.split('@')[0].split(':')[0] : (inst.owner && !inst.owner.includes('@') ? inst.owner : null)),
            status: mapStatus(inst.status, inst.connectionStatus),
            qr_code: inst.qrcode || inst.base64 || null,
            profile_pic_url: inst.profilePictureUrl || null,
            webhook_url: inst.webhook || (inst.webhookUrl ? inst.webhookUrl : null),
            settings: inst.settings ? JSON.stringify(inst.settings) : null,
            last_seen: now,
            user_id: inst.owner || inst.user_id || FALLBACK_USER_ID,
            created_at: now,
            updated_at: now
        });
    }
}

for (const item of items) {
    const data = item.json;
    if (Array.isArray(data)) {
        for (const subItem of data) {
            processInstance(subItem.instance || subItem);
        }
    } else {
        processInstance(data.instance || data);
    }
}

return [{ json: { data: supabaseData } }];

// Use $input.all() to get all items from the Merge node (Append mode)
const items = $input.all();

if (items.length === 0) return [];

// Extract instanceId from the first item. 
// Prioritize 'instance_id' (injected Supabase ID) over 'instanceId' (Evolution ID).
let instanceId = items[0].json.instance_id;

// Fallback: Try to get it directly from Split Instances node if missing in items
if (!instanceId) {
    try {
        instanceId = $('Split Instances').first().json.id;
    } catch (e) {
        // ignore
    }
}

if (!instanceId) {
    throw new Error(`Instance ID is missing! Value: ${instanceId}, keys: ${Object.keys(items[0].json).join(', ')}`);
}

const contactsMap = {};

function getPhone(item) {
    if (item.phoneNumber) return item.phoneNumber;
    // Removed item.number check as it seems to be an internal ID

    // Prioritize explicit JID fields
    const jid = item.remoteJid || item.jid;
    if (jid && jid.includes('@')) {
        return jid.split('@')[0].split(':')[0];
    }

    // Fallback to id only if it looks like a JID
    if (item.id && item.id.includes('@')) {
        return item.id.split('@')[0].split(':')[0];
    }

    return null;
}

items.forEach((itemObj, index) => {
    const item = itemObj.json;
    const id = item.remoteJid || item.id || item.jid;
    const phone = getPhone(item);
    if (!phone) return;

    const isGroup = item.type === 'group' || item.isGroup === true || (id && id.includes('@g.us'));
    const pic = item.profilePicUrl || item.profilePictureUrl || null;

    // Determine name properties
    // Groups usually have 'subject'. Contacts have 'name', 'pushName', 'notify'.
    const name = item.subject || item.name || item.pushName || item.notify || phone;
    const pushName = item.pushName || item.notify || item.subject;

    if (!contactsMap[phone]) {
        contactsMap[phone] = {
            json: {
                phone: phone,
                name: name,
                push_name: pushName,
                profile_pic_url: pic,
                is_group: isGroup,
                group_metadata: isGroup ? item : null,
                instance_id: instanceId
            },
            // Pair with the current input item to preserve lineage as much as possible,
            // or pair with the first item [0] if we want to treat them all as children of the batch.
            // Since we are aggregating, pairing with 0 is safer for the 'Split Instances' reference.
            pairedItem: { item: 0 }
        };
    } else {
        const existing = contactsMap[phone].json;

        // Update profile pic if new one is present
        if (pic) existing.profile_pic_url = pic;

        // Update name if we found a better one (e.g. not just the phone number)
        if (name && name !== phone) existing.name = name;

        // If it's a group, ensure is_group is true and update metadata if this item looks like it has more info (e.g. subject)
        if (isGroup) {
            existing.is_group = true;
            if (item.subject || item.participants) {
                existing.group_metadata = item;
            }
        }
    }
});

return Object.values(contactsMap);

const instanceId = $('Split Instances').item.json.id;
const instanceName = $('Split Instances').item.json.name;

if (!instanceName) {
    throw new Error("Instance Name is missing! Check Split Instances node.");
}

// Use $input.all() to get the output from 'Upsert Contacts' which has the Supabase UUIDs
// Supabase returns an array of inserted rows, so we flat() to get a single list of objects
const contacts = $input.all().map(i => i.json).flat();
const contactMap = {};

contacts.forEach(c => {
    if (c.phone && c.id) {
        // Store both ID and profile pic URL
        contactMap[c.phone] = {
            id: c.id,
            profile_pic_url: c.profile_pic_url
        };
    }
});

return [{
    json: { contactMap, instanceId, instanceName },
    pairedItem: { item: 0 }
}];

const instanceId = $('Split Instances').item.json.id;
const contactMap = $('Create Contact Map').item.json.contactMap;
const chats = $input.all().map(i => i.json);

const normalizedChats = [];

// DEBUG: Check if map is populated
const mapKeys = Object.keys(contactMap || {});
if (mapKeys.length === 0) {
    throw new Error("Contact Map is empty!");
}

chats.forEach(chat => {
    if (Array.isArray(chat)) {
        chat.forEach(c => processChat(c));
    } else {
        processChat(chat);
    }
});

function processChat(c) {
    // IMPORTANT: Use remoteJid (WhatsApp chat ID), NOT id (Evolution's internal ID)
    const remoteJid = c.remoteJid || c.id;
    if (!remoteJid) return;

    const phone = remoteJid.split('@')[0].split(':')[0];

    // Get contact ID from map (now contains object {id, profile_pic_url})
    const contactData = contactMap[phone];
    const contactId = contactData ? contactData.id : null;

    // DEBUG: Log first failure
    if (!contactId && normalizedChats.length === 0) {
         // throw new Error(`Debug: Phone ${phone} not found in map. Map keys sample: ${mapKeys.slice(0, 5).join(', ')}`);
    }

    // Extract last message content
    const lastMsgObj = c.lastMessage;
    const content = lastMsgObj?.message?.conversation || 
                    lastMsgObj?.message?.extendedTextMessage?.text || 
                    lastMsgObj?.message?.imageMessage?.caption || 
                    (lastMsgObj ? 'Media/Other' : null);

    normalizedChats.push({
        chat_id: remoteJid,
        instance_id: instanceId,
        contact_id: contactId,
        unread_count: c.unreadCount || 0,
        last_message_time: c.conversationTimestamp ? new Date(c.conversationTimestamp * 1000).toISOString() : new Date().toISOString(),
        last_message: content
    });
}

// DEBUG: Throw error to see output
// const sample = normalizedChats.find(c => c.contact_id === null);
// if (sample) {
//    throw new Error(`Failed to map contact! Chat: ${sample.chat_id}, Phone: ${sample.chat_id.split('@')[0]}, Map Sample: ${mapKeys.slice(0, 5)}`);
// }

return normalizedChats;

const instanceName = $('Split Instances').item.json.name;
const items = $input.all();
const chatMap = {};

return items.map((item, index) => {
    // Supabase returns an array of inserted rows, take the first one
    const c = Array.isArray(item.json) ? item.json[0] : item.json;
    chatMap[c.chat_id] = c.id;
    c.instanceName = instanceName;
    return {
        json: c,
        pairedItem: { item: index }
    };
});

const instanceId = $('Split Instances').item.json.id;
const chatUuid = $('Split Chats').item.json.id;
const chatRemoteJid = $('Split Chats').item.json.chat_id;
const contactMapData = $('Create Contact Map').first().json.contactMap;
const items = $input.all();

const normalizedMessages = [];

// Map Evolution API message types to database-valid types
function mapMessageType(evoType) {
    const typeMap = {
        'conversation': 'text',
        'extendedTextMessage': 'text',
        'imageMessage': 'image',
        'videoMessage': 'video',
        'audioMessage': 'audio',
        'documentMessage': 'document',
        'stickerMessage': 'sticker',
        'locationMessage': 'location',
        'contactMessage': 'contact',
        'protocolMessage': 'system',
        'ephemeralMessage': 'system',
        'ptvMessage': 'video'
    };
    return typeMap[evoType] || 'text';
}

items.forEach((itemObj, index) => {
    const data = itemObj.json;

    // Handle Evolution API paginated response structure
    let messages = [];
    if (data.messages && data.messages.records) {
        // Paginated response: { messages: { records: [...] } }
        messages = data.messages.records;
    } else if (Array.isArray(data)) {
        // Direct array
        messages = data;
    } else {
        // Single message object
        messages = [data];
    }

    messages.forEach(m => processMessage(m, index));
});

function processMessage(m, parentIndex) {
    const key = m.key || {};
    const messageId = key.id;
    if (!messageId) return;

    // Extract participant
    // For group chats, participant is in key.participant
    // For direct chats, it might be key.remoteJid (if not from me)
    let participant = key.participant || key.remoteJid;

    // If participant has @lid, we might want to use participantAlt (phone number) if available
    // But usually the contact map is keyed by phone number (user ID part of JID)
    if (key.participantAlt) {
        participant = key.participantAlt;
    }

    const participantPhone = participant ? participant.split('@')[0].split(':')[0] : null;

    // Get contact data from map (now contains id and profile_pic_url)
    const contactData = participantPhone ? contactMapData[participantPhone] : null;
    const contactId = contactData ? contactData.id : null;
    const thumbnailPath = contactData ? contactData.profile_pic_url : null;

    const content = m.message?.conversation || m.message?.extendedTextMessage?.text || m.message?.imageMessage?.caption || 'Media/Other';

    // Get the raw message type from Evolution API
    const rawType = Object.keys(m.message || {})[0] || 'conversation';
    const messageType = mapMessageType(rawType);

    // Extract media info if available
    let mediaUrl = null;
    let mediaPath = null;
    let mediaSize = null;
    let mediaMimeType = null;

    const mediaMsg = m.message?.[rawType];
    if (mediaMsg && typeof mediaMsg === 'object') {
        mediaUrl = mediaMsg.url || null;
        mediaPath = mediaMsg.directPath || null;
        mediaSize = mediaMsg.fileLength?.low || mediaMsg.fileLength || null;
        mediaMimeType = mediaMsg.mimetype || null;
    }

    // Extract quoted message ID
    const contextInfo = m.message?.[rawType]?.contextInfo || m.contextInfo;
    const quotedMessageId = contextInfo?.stanzaId || null;

    // Construct Metadata object
    const metadata = {
        media: (mediaUrl || mediaPath || mediaMimeType) ? {
            url: mediaUrl,
            path: mediaPath,
            mimetype: mediaMimeType
        } : null,
        sticker: m.message?.stickerMessage || null,
        location: m.message?.locationMessage || null,
        pushName: m.pushName || null,
        rawMessage: m,
        webhookSource: 'evolution'
    };

    normalizedMessages.push({
        json: {
            message_id: messageId,
            chat_id: chatRemoteJid,
            chat_table_id: chatUuid,
            contact_id: contactId,
            instance_id: instanceId,
            content: content,
            from_me: key.fromMe || false,
            timestamp_msg: m.messageTimestamp ? new Date(m.messageTimestamp * 1000).toISOString() : new Date().toISOString(),
            status: m.status || 'delivered',
            message_type: messageType,
            thumbnail_path: thumbnailPath,
            media_url: mediaUrl,
            media_path: mediaPath,
            media_size: mediaSize,
            media_mime_type: mediaMimeType,
            quoted_message_id: quotedMessageId,
            participant: participant, // Populate the participant column
            metadata: metadata // Populate the metadata column
        },
        pairedItem: { item: parentIndex }
    });
}

return normalizedMessages;
