const supabaseData = [];
const FALLBACK_USER_ID = '00000000-0000-0000-0000-000000000000';

function mapStatus(status, connectionStatus) {
    let s = (connectionStatus || status || '').toLowerCase();
    if (s === 'open' || s === 'connected') return 'connected';
    if (s === 'close' || s === 'disconnected') return 'disconnected';
    if (s === 'connecting') return 'connecting';
    if (s === 'qr' || s === 'qrcode') return 'qr_code';
    return 'disconnected';
}

function processInstance(inst) {
    const name = inst.instanceName || inst.name;

    if (name) {
        supabaseData.push({
            name: name,
            status: mapStatus(inst.status, inst.connectionStatus),
            evolution_instance_id: inst.instanceId || inst.id || inst.name,
            user_id: inst.owner || inst.user_id || FALLBACK_USER_ID,
            phone: inst.ownerJid ? inst.ownerJid.split('@')[0] : null,
            profile_pic_url: inst.profilePictureUrl || null,
            // Mapped columns (raw_base_config removed as requested)
            qr_code: inst.qrcode || inst.base64 || null,
            webhook_url: inst.webhook || (inst.webhookUrl ? inst.webhookUrl : null),
            settings: inst.settings ? JSON.stringify(inst.settings) : null,
        });
    }
}

for (const item of items) {
    const data = item.json;
    if (Array.isArray(data)) {
        for (const subItem of data) {
            processInstance(subItem.instance || subItem);
        }
    } else {
        processInstance(data.instance || data);
    }
}

return [{ json: { data: supabaseData } }];
